import React from 'react';
import "./NotfoundPage.css"
import { serverRoot } from '../../App';
import "./UploadPage.css"

class UploadPage extends React.Component
{


    GetSrc = () => {
        if(this.state.file == null) return null;

        const url = URL.createObjectURL(this.state.file);
        console.log(url)

        return url;
    }

    UploadFile = () => 
    {
        const formData = new FormData();
        console.log(this.state.file);
        formData.append('file', this.state.file);



        console.log(formData)
        fetch(`${serverRoot}/api/video/upload`, {
            method: 'POST',
            body: formData
        })
        .then(response => {
            if (response.ok) {
                console.log('File uploaded successfully');
            } else {
                console.error('Failed to upload file');
            }
        })
        .catch(error => {
            console.error('Error:', error);
        });
    }

    state = 
    {
        file : null,
    }

    render(){
        
        return (<div className='NotfoundPage'>
            <div onClick={()=>{window.location.replace(`${window.location.origin}/`)}}>
                <i className="Announce">Page NotFounded!</i>
                <br/>
                <i className="ReturnLink"
                    >Please click here to go back to home!</i>
            </div>


            <VideoInterface updater={v => this.setState(cur=>{return {file : v}})}></VideoInterface>



            
            <button type="button" onClick={()=>this.UploadFile()}>Upload File</button>
        </div>)
    }

}

class VideoInterface extends React.Component
{

    render()
    {
        return(
        <form className="InterfaceFile" encType="multipart/form-data">
            {this.state.file == null? (
                <video className="UploadThumbVideo" src={ this.GetSrc() }></video>
            ) : (
                <video className="UploadThumbVideo" src={ this.GetSrc() }></video>
            )}
            <input className="InputFile" type="file" name="fileInput" id="fileInput" accept=".mp4"
                onChange={e => this.props.updater( e.target.files[0])
                    
                }></input>
        </form>)
    }
}


export default UploadPage
