import React from 'react';
import "./Card.css"

class Card extends React.Component
{
    state = 
    {
        size : [0,0],
        videoCode : "",
        image : "https://static0.gamerantimages.com/wordpress/wp-content/uploads/2024/02/helldivers-2-review.jpg",
        name : ""
    }

    constructor(props)
    {
        super(props)
        this.setState(current => {this.props.size = [props.width, props.height]})
        this.setState(current => {this.props.videoCode = props.videoCode})
    }


    render()
    {
        return(<div className="items">
            <img className = "Thumbnail"
                style = {{
                    minWidth : this.state.size[0],
                    height : this.state.size[1]
                }}
                src = {this.state.image}
            ></img>
            {this.state.name}
        </div>)
    }


}

export default Card;
