import React from "react";
import "./SideBar.css"
import testIcon from "../Resources/magnifier.png"

class SideBar extends React.Component
{   
    render()
    {
        console.log(this.props.isOpened);

        return (
        <div className={this.props.isOpened? "SideBar Open" : "SideBar"}>
            <SideBarUnit name={"hello"} direction={"../a"} icon={testIcon}></SideBarUnit>
            <div> I slide into view </div>
            <div> Me Too! </div>
            <div> Me Three! </div>
        </div>
        );
    }
}



class SideBarUnit extends React.Component
{
    state = 
    {
        mouseOn : false,
    }

    render()
    {
        return (<div
            onMouseEnter={ ()=>{ this.setState({ mouseOn : true}) }}
            onMouseLeave={ ()=>{ this.setState({ mouseOn : false}) }}
            style={{
                display: "flex",
                width: "100%",
                height : "25px",
                margin : "2px",
                alignItems : "center",

            }}>
            <img style={{
                    height: "25px",
                    filter: "invert()",
                }}
                src={this.props.icon}></img>
            <p onClick={()=>{ window.location.replace(`${window.location.origin}/${direction}}`) }}
            >{this.props.name}</p>
        </div>)
    }
}

export default SideBar;
