import React from 'react';
import "./Searcher.css"


class Searcher extends React.Component
{   
    state = 
    {
        isHighlightTextbox : false,
        isHighlightButton : false,
    }




    render()
    {
        return (<div className = "Searcher">
            <input className="TextBox"
                type="text"
                placeholder={"Let me search videos for you!"}
                onFocus={()=>{this.setState(current => {return { isHighlightTextbox : true}} )}}
                onBlur={()=>{this.setState(current => {return { isHighlightTextbox : false}} )}}
                style={{
                    outlineColor : this.state.isHighlightTextbox? "#006eff" : "#34343f"
                }}
                ></input>
            <button className="ConfirmBtn"
                onMouseEnter={()=>{this.setState(current => {return { isHighlightButton : true}} )}}
                onMouseLeave={()=>{this.setState(current => {return { isHighlightButton : false}} )}}
                style={{
                    backgroundColor : this.state.isHighlightButton? "#006eff" : "#34343f"
                }}
                >시발!</button>
        </div>)
    }

}

export default Searcher;
