import React from 'react';
import { ApiLibrary } from '../../Library';
import { useParams } from 'react-router-dom';
import { serverRoot } from '../../App.js'


import "./NotfoundPage.css"



function withParams(Component)
{
    return (props) => <Component{...props} params={useParams()}/>;
}


class ChannelPage extends React.Component
{
    constructor(props)
    {
        super(props)

        this.state = {
            ...this.state,
            chId: props.params.id
        };

        const handleResize = () => {
            this.setState(
                current => {return {windowSize : [window.innerWidth, window.innerHeight]}} 
            )
        }
        window.addEventListener("resize", handleResize);
        
    }

    render(){
    
        return (<div className='NotfoundPage'>
            <div onClick={()=>{window.location.replace(`${window.location.origin}/`)}}>
                <i className="Announce">Page NotFounded!</i>
                <br/>
                <i className="ReturnLink"
                    >Please click here to go back to home!</i>
            </div>
        </div>)
    }
}

export default withParams(ChannelPage)
