import React from 'react';
import "./NotfoundPage.css"
import { serverRoot } from '../../App';

class UploadPage extends React.Component
{

    UploadFile = () => 
    {
        const formData = new FormData();
        console.log(this.state.file);
        formData.append('file', this.state.file);

        console.log(formData)
        fetch(`${serverRoot}/api/video/upload`, {
            method: 'POST',
            body: formData
        })
        .then(response => {
            if (response.ok) {
                console.log('File uploaded successfully');
            } else {
                console.error('Failed to upload file');
            }
        })
        .catch(error => {
            console.error('Error:', error);
        });
    }

    state = 
    {
        file : null,
    }

    render(){
    
        return (<div className='NotfoundPage'>
            <div onClick={()=>{window.location.replace(`${window.location.origin}/`)}}>
                <i className="Announce">Page NotFounded!</i>
                <br/>
                <i className="ReturnLink"
                    >Please click here to go back to home!</i>
            </div>


            <form id="uploadForm" encType="multipart/form-data">
                <video src={()=>{
                    if(this.state.file == null) return null;

                    const url = URL.createObjectURL(this.state.file);
                    console.log(url)
                    return url

                }}></video>
                <input type="file" name="fileInput" id="fileInput" accept=".mp4"
                    onChange={e => {this.setState(cur=>{return {file : e.target.files[0]}})}}></input>
                <button type="button" onClick={()=>this.UploadFile()}>Upload File</button>
            </form>
        </div>)
    }

}

export default UploadPage
